export const initialState = {
    user: null,
    playlist:[],
    playing: false,
    item: null,
    token:null
}

const reducer = (state , action)=>{
console.log('dddddddddddddddddddddddd',action);

switch (action.type){
    case 'SET_USER':
        return {
            ...state,
            user: action.user
        }

default :
return state;        
}

}
export default reducer
